import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import '../styles/BookingPage.css'; // Correct path to styles

const BookingPage = () => {
  const { register, handleSubmit } = useForm(); // For form validation
  const [pickupLocation, setPickupLocation] = useState('');
  const [destinationLocation, setDestinationLocation] = useState('');
  const [vehicleDetails, setVehicleDetails] = useState({
    registrationNumber: '',
    vehicleType: '',
    weight: ''
  });
  const [otherInfo, setOtherInfo] = useState(''); // State for other information field
  const [termsAccepted, setTermsAccepted] = useState(false);

  // Handle form submission
  const onSubmit = (data) => {
    if (!termsAccepted) {
      alert('You must accept the terms and conditions before proceeding.');
      return;
    }

    const formData = {
      pickupLocation: data.pickupLocation,
      destinationLocation: data.destinationLocation,
      vehicleDetails: {
        registrationNumber: data.registrationNumber,
        vehicleType: data.vehicleType,
        weight: data.weight
      },
      serviceDate: data.serviceDate,
      datesFrom: data.datesFrom,
      datesTo: data.datesTo,
      vehicleCondition: data.vehicleCondition,
      hiringPersonName: data.hiringPersonName,
      hiringPersonPhone: data.hiringPersonPhone,
      driverName: data.driverName,
      driverPhone: data.driverPhone,
      otherInfo: data.otherInfo // Add other information to the form data
    };

    // Send form data to backend or email
    axios.post('YOUR_API_URL', formData) // Replace with your API endpoint
      .then(response => {
        console.log('API Response:', response.data);
      })
      .catch(error => {
        console.error('API Error:', error);
      });

    // Send email to robertmotoringconsult@outlook.com
    const emailBody = `
      New Booking Request:
      
      Pickup Location: ${data.pickupLocation}
      Destination Location: ${data.destinationLocation}
      Vehicle Details:
        Registration Number: ${data.registrationNumber}
        Vehicle Type: ${data.vehicleType}
        Weight: ${data.weight}
      Service Date: ${data.serviceDate}
      Dates From: ${data.datesFrom}
      Dates To: ${data.datesTo}
      Condition of Vehicle: ${data.vehicleCondition}
      Hiring Person Name: ${data.hiringPersonName}
      Hiring Person Phone: ${data.hiringPersonPhone}
      Driver Name: ${data.driverName}
      Driver Phone: ${data.driverPhone}
      Any Other Information: ${data.otherInfo}
    `;

    sendEmail('robertmotoringconsult@outlook.com', 'New Booking Request', emailBody);

    // Send WhatsApp message to your partner (bolded text for action)
    const whatsAppMessage = `
      *NEW FLATBED SERVICE REQUEST*:
      *Pickup Location*: ${data.pickupLocation}
      *Destination Location*: ${data.destinationLocation}
      *Vehicle Details*:
        *Registration Number*: ${data.registrationNumber}
        *Vehicle Type*: ${data.vehicleType}
        *Weight*: ${data.weight}
      *Service Date*: ${data.serviceDate}
      *Dates From*: ${data.datesFrom}
      *Dates To*: ${data.datesTo}
      *Condition of Vehicle*: ${data.vehicleCondition}
      *Hiring Person Name*: ${data.hiringPersonName}
      *Hiring Person Phone*: ${data.hiringPersonPhone}
      *Driver Name*: ${data.driverName}
      *Driver Phone*: ${data.driverPhone}
      *Any Other Information*: ${data.otherInfo}
      
      Please review and act on this request immediately.
    `;

    const encodedMessage = encodeURIComponent(whatsAppMessage);
    const partnerWhatsAppUrl = `https://wa.me/+254729585035?text=${encodedMessage}`;
    
    // Open WhatsApp in a new window for your partner to receive the message
    window.open(partnerWhatsAppUrl, '_blank');
  };

  return (
    <div className="booking-page">
      <h1>Book a Flatbed Truck</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        {/* Pickup Location Input */}
        <div className="form-group">
          <label htmlFor="pickupLocation">Pickup Location:</label>
          <input
            type="text"
            id="pickupLocation"
            {...register('pickupLocation')}
            value={pickupLocation}
            onChange={(e) => setPickupLocation(e.target.value)}
            placeholder="Enter Pickup Location"
          />
        </div>

        {/* Destination Location Input */}
        <div className="form-group">
          <label htmlFor="destinationLocation">Destination Location:</label>
          <input
            type="text"
            id="destinationLocation"
            {...register('destinationLocation')}
            value={destinationLocation}
            onChange={(e) => setDestinationLocation(e.target.value)}
            placeholder="Enter Destination Location"
          />
        </div>

        {/* Vehicle Details Inputs */}
        <div className="form-group">
          <label htmlFor="registrationNumber">Vehicle Registration Number:</label>
          <input
            type="text"
            id="registrationNumber"
            {...register('registrationNumber')}
            value={vehicleDetails.registrationNumber}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, registrationNumber: e.target.value })}
            placeholder="Enter Vehicle Registration Number"
          />
        </div>

        <div className="form-group">
          <label htmlFor="vehicleType">Vehicle Type:</label>
          <input
            type="text"
            id="vehicleType"
            {...register('vehicleType')}
            value={vehicleDetails.vehicleType}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, vehicleType: e.target.value })}
            placeholder="Enter Vehicle Type"
          />
        </div>

        <div className="form-group">
          <label htmlFor="weight">Vehicle Weight:</label>
          <input
            type="number"
            id="weight"
            {...register('weight')}
            value={vehicleDetails.weight}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, weight: e.target.value })}
            placeholder="Enter Vehicle Weight"
          />
        </div>

        {/* Special Instructions / Other Information */}
        <div className="form-group">
          <label htmlFor="otherInfo">Any Other Information:</label>
          <textarea
            id="otherInfo"
            {...register('otherInfo')}
            value={otherInfo}
            onChange={(e) => setOtherInfo(e.target.value)}
            placeholder="Enter special instructions (e.g., pickup/delivery details)"
          />
        </div>

        {/* Submit Button */}
        <div className="form-group">
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default BookingPage;
