import React, { useState } from 'react';
import '../styles/BookingPage.css'; // Correct path to the CSS file in src/styles/
import logo from '../assets/logo.png'; // Path to the logo image in src/assets/
import flatbedBanner from '../assets/flatbed_banner.png'; // Path to the flatbed homepage photo in src/assets/

const BookingPage = () => {
  const [userName, setUserName] = useState('');
  const [carRegistration, setCarRegistration] = useState('');
  const [carType, setCarType] = useState('');
  const [carWeight, setCarWeight] = useState('');
  const [pickupLocation, setPickupLocation] = useState('');
  const [deliveryLocation, setDeliveryLocation] = useState('');
  const [termsAccepted, setTermsAccepted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (termsAccepted) {
      alert("Form submitted successfully!");
    } else {
      alert("You must accept the terms and conditions.");
    }
  };

  return (
    <div className="booking-container">
      <div className="logo-container">
        <img src={logo} alt="Robert Motoring Consult" className="logo" />
      </div>
      <h1>Book a Car Rescue Service</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            required
          />
        </label>
        <label>
          Car Registration:
          <input
            type="text"
            value={carRegistration}
            onChange={(e) => setCarRegistration(e.target.value)}
            required
          />
        </label>
        <label>
          Car Type:
          <input
            type="text"
            value={carType}
            onChange={(e) => setCarType(e.target.value)}
            required
          />
        </label>
        <label>
          Car Weight (KG):
          <input
            type="number"
            value={carWeight}
            onChange={(e) => setCarWeight(e.target.value)}
            required
          />
        </label>
        <label>
          Pickup Location:
          <input
            type="text"
            value={pickupLocation}
            onChange={(e) => setPickupLocation(e.target.value)}
            required
          />
        </label>
        <label>
          Delivery Location:
          <input
            type="text"
            value={deliveryLocation}
            onChange={(e) => setDeliveryLocation(e.target.value)}
            required
          />
        </label>
        <div>
          <input
            type="checkbox"
            checked={termsAccepted}
            onChange={() => setTermsAccepted(!termsAccepted)}
          />
          <label>
            I accept the{' '}
            <a href="/terms" target="_blank" rel="noopener noreferrer">
              Terms and Conditions
            </a>
          </label>
        </div>
        <button type="submit" disabled={!termsAccepted}>
          Submit Booking
        </button>
      </form>

      <div className="flatbed-banner-container">
        <img src={flatbedBanner} alt="Flatbed carrying car" className="flatbed-banner" />
      </div>
    </div>
  );
};

export default BookingPage;
