import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import '../styles/BookingPage.css';  // Corrected import path

const BookingPage = () => {
  const history = useHistory();
  const [formData, setFormData] = useState({
    userName: '',
    userEmail: '',
    carRegistration: '',
    carType: '',
    carWeight: '',
    pickupLocation: '',
    deliveryLocation: '',
    driverName: '',
    driverPhone: '',
    serviceDate: '',
    dateFrom: '',
    dateTo: '',
    condition: '',
    otherInfo: '',
    acceptTerms: false,
  });

  // Handle form data changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle checkbox change for terms acceptance
  const handleCheckboxChange = (e) => {
    setFormData({
      ...formData,
      acceptTerms: e.target.checked,
    });
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.acceptTerms) {
      alert('You must accept the Terms and Conditions to continue.');
      return;
    }

    try {
      // Send booking data to the backend
      await axios.post('/api/booking', formData);

      // Send confirmation email to the user
      await axios.post('/api/send-confirmation', { email: formData.userEmail, formData });

      // Redirect to confirmation page after successful booking
      history.push('/confirmation');
    } catch (error) {
      console.error('Error submitting booking:', error);
    }
  };

  return (
    <div className="booking-page">
      <h1>Book a Flatbed Truck</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="userName">Your Name</label>
          <input
            type="text"
            id="userName"
            name="userName"
            value={formData.userName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="userEmail">Your Email</label>
          <input
            type="email"
            id="userEmail"
            name="userEmail"
            value={formData.userEmail}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="carRegistration">Car Registration Number</label>
          <input
            type="text"
            id="carRegistration"
            name="carRegistration"
            value={formData.carRegistration}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="carType">Car Type</label>
          <input
            type="text"
            id="carType"
            name="carType"
            value={formData.carType}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="carWeight">Car Weight (kg)</label>
          <input
            type="number"
            id="carWeight"
            name="carWeight"
            value={formData.carWeight}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="pickupLocation">Pickup Location</label>
          <input
            type="text"
            id="pickupLocation"
            name="pickupLocation"
            value={formData.pickupLocation}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="deliveryLocation">Delivery Location</label>
          <input
            type="text"
            id="deliveryLocation"
            name="deliveryLocation"
            value={formData.deliveryLocation}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="driverName">Driver Name</label>
          <input
            type="text"
            id="driverName"
            name="driverName"
            value={formData.driverName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="driverPhone">Driver Phone Number</label>
          <input
            type="text"
            id="driverPhone"
            name="driverPhone"
            value={formData.driverPhone}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="serviceDate">Date Service is Required</label>
          <input
            type="date"
            id="serviceDate"
            name="serviceDate"
            value={formData.serviceDate}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="dateFrom">Date From</label>
          <input
            type="date"
            id="dateFrom"
            name="dateFrom"
            value={formData.dateFrom}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="dateTo">Date To</label>
          <input
            type="date"
            id="dateTo"
            name="dateTo"
            value={formData.dateTo}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="condition">Condition of the Car Upon Collection</label>
          <textarea
            id="condition"
            name="condition"
            value={formData.condition}
            onChange={handleChange}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="otherInfo">Any Other Information</label>
          <textarea
            id="otherInfo"
            name="otherInfo"
            value={formData.otherInfo}
            onChange={handleChange}
          ></textarea>
        </div>
        <div className="form-group">
          <input
            type="checkbox"
            id="acceptTerms"
            name="acceptTerms"
            checked={formData.acceptTerms}
            onChange={handleCheckboxChange}
            required
          />
          <label htmlFor="acceptTerms">
            I accept the <a href="/terms" target="_blank" rel="noopener noreferrer">Terms and Conditions</a>
          </label>
        </div>
        <button type="submit" disabled={!formData.acceptTerms}>
          Submit Booking
        </button>
      </form>
    </div>
  );
};

export default BookingPage;
