import React, { useState } from 'react';
import '../styles/BookingForm.css'; // Corrected path to the stylesheet

const BookingForm = () => {
  const [vehicleDetails, setVehicleDetails] = useState({
    registrationNumber: '',
    vehicleType: '',
    weight: '',
  });
  const [userDetails, setUserDetails] = useState({
    name: '',
    phone: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name in vehicleDetails) {
      setVehicleDetails({ ...vehicleDetails, [name]: value });
    } else {
      setUserDetails({ ...userDetails, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Booking details submitted');
  };

  return (
    <form className="booking-form" onSubmit={handleSubmit}>
      <h3>Booking Form</h3>

      <div className="form-group">
        <label>Name</label>
        <input
          type="text"
          name="name"
          value={userDetails.name}
          onChange={handleInputChange}
          required
        />
      </div>

      <div className="form-group">
        <label>Phone Number</label>
        <input
          type="text"
          name="phone"
          value={userDetails.phone}
          onChange={handleInputChange}
          required
        />
      </div>

      <div className="form-group">
        <label>Vehicle Registration Number</label>
        <input
          type="text"
          name="registrationNumber"
          value={vehicleDetails.registrationNumber}
          onChange={handleInputChange}
          required
        />
      </div>

      <div className="form-group">
        <label>Vehicle Type</label>
        <input
          type="text"
          name="vehicleType"
          value={vehicleDetails.vehicleType}
          onChange={handleInputChange}
          required
        />
      </div>

      <div className="form-group">
        <label>Vehicle Weight</label>
        <input
          type="text"
          name="weight"
          value={vehicleDetails.weight}
          onChange={handleInputChange}
          required
        />
      </div>

      <button type="submit">Submit Booking</button>
    </form>
  );
};

export default BookingForm;
