import React from 'react';
import '../styles/Terms.css';  // Corrected import path

const Terms = () => {
  return (
    <div className="terms-page">
      <h1>Terms and Conditions</h1>
      <p>By booking our car rescue service, you agree to the following terms and conditions:</p>
      <ul>
        <li>
          <strong>Service Availability:</strong> Our flatbed trucks are available 24/7 depending on location.
        </li>
        <li>
          <strong>Payment:</strong> A non-refundable booking fee of KES 1,500 is required to confirm your booking.
        </li>
        <li>
          <strong>Responsibility:</strong> We will not accept or participate in any illegal activity, and any such activity will be the responsibility of the person(s) hiring the service.
        </li>
        <li>
          <strong>Booking Confirmation:</strong> Your booking will be confirmed after the payment is processed.
        </li>
      </ul>
      <p>For any issues or complaints, please contact us via email: <strong>robertmotoringconsult@outlook.com</strong>.</p>
    </div>
  );
};

export default Terms;
