import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './BookingPage.css';  // Import the CSS for styling

const BookingPage = () => {
  const [userName, setUserName] = useState('');
  const [carDetails, setCarDetails] = useState('');
  const [isTermsAccepted, setIsTermsAccepted] = useState(false);  // New state for Terms checkbox

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!isTermsAccepted) {
      alert('Please accept the terms and conditions to proceed.');
      return;
    }
    alert('Booking submitted!');
  };

  return (
    <div className="booking-container">
      <h1>Car Rescue Service</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Your Name:
          <input
            type="text"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            required
          />
        </label>
        <label>
          Car Details:
          <input
            type="text"
            value={carDetails}
            onChange={(e) => setCarDetails(e.target.value)}
            required
          />
        </label>
        <label>
          <input
            type="checkbox"
            checked={isTermsAccepted}
            onChange={() => setIsTermsAccepted(!isTermsAccepted)} // Toggle checkbox state
          />
          I accept the <Link to="/terms">Terms and Conditions</Link>
        </label>
        <button type="submit">Submit Booking</button>
      </form>
    </div>
  );
};

export default BookingPage;
