// src/pages/BookingPage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles/BookingPage.css'; // Correct path to the CSS file

import logo from '../assets/logo.png'; // Path to logo

const BookingPage = () => {
  const [termsAccepted, setTermsAccepted] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    carModel: '',
    registrationNumber: '',
    conditionDescription: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleTermsChange = (e) => {
    setTermsAccepted(e.target.checked);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (termsAccepted) {
      // Handle form submission logic here
      alert('Booking submitted!');
    } else {
      alert('Please accept the terms and conditions.');
    }
  };

  return (
    <div className="booking-page">
      <img src={logo} alt="Robert Motoring Consult Logo" className="logo" />
      <h1>Book Your Flatbed Rescue Service</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Phone</label>
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Car Model</label>
          <input
            type="text"
            name="carModel"
            value={formData.carModel}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Registration Number</label>
          <input
            type="text"
            name="registrationNumber"
            value={formData.registrationNumber}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Condition Description</label>
          <textarea
            name="conditionDescription"
            value={formData.conditionDescription}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>
            <input
              type="checkbox"
              checked={termsAccepted}
              onChange={handleTermsChange}
            />
            I accept the{' '}
            <Link to="/terms" target="_blank" rel="noopener noreferrer">
              Terms and Conditions
            </Link>
          </label>
        </div>
        <button type="submit" disabled={!termsAccepted}>
          Submit Booking
        </button>
      </form>
    </div>
  );
};

export default BookingPage;
