import React, { useCallback, useRef, useState } from "react";
import {
  GoogleMap,
  useLoadScript,
  Marker,
  DirectionsRenderer,
} from "@react-google-maps/api";

const libraries = ["places"]; // Enables search functionality
const mapContainerStyle = {
  width: "100%",
  height: "500px",
};
const center = {
  lat: -1.286389, // Nairobi
  lng: 36.817223,
};

const options = {
  disableDefaultUI: false,
  zoomControl: true,
};

const MapContainer = () => {
  const [pickup, setPickup] = useState(null);
  const [delivery, setDelivery] = useState(null);
  const [directions, setDirections] = useState(null);
  const mapRef = useRef();

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: "AIzaSyCmuqLdW7dcMlz0kFb9yW4mNYYkFLUkN4M",
    libraries,
  });

  const onMapLoad = useCallback((map) => {
    mapRef.current = map;
  }, []);

  const handleMapClick = (e) => {
    if (!pickup) {
      setPickup({ lat: e.latLng.lat(), lng: e.latLng.lng() });
    } else if (!delivery) {
      setDelivery({ lat: e.latLng.lat(), lng: e.latLng.lng() });
    }
  };

  const calculateRoute = () => {
    if (!pickup || !delivery) return;

    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin: pickup,
        destination: delivery,
        travelMode: window.google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          setDirections(result);
        } else {
          console.error(`Error fetching directions: ${status}`);
        }
      }
    );
  };

  const resetMap = () => {
    setPickup(null);
    setDelivery(null);
    setDirections(null);
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps...</div>;

  return (
    <div>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={10}
        center={pickup || center}
        options={options}
        onClick={handleMapClick}
        onLoad={onMapLoad}
      >
        {pickup && <Marker position={pickup} label="Pickup" />}
        {delivery && <Marker position={delivery} label="Delivery" />}
        {directions && <DirectionsRenderer directions={directions} />}
      </GoogleMap>

      <div style={{ marginTop: "10px" }}>
        {!directions && pickup && delivery && (
          <button onClick={calculateRoute}>Calculate Route</button>
        )}
        {(pickup || delivery) && <button onClick={resetMap}>Reset</button>}
      </div>
    </div>
  );
};

export default MapContainer;
