import React from "react";
import "./Terms.css";
import logo from "../assets/logo.png";

const Terms = () => {
  return (
    <div className="terms-container">
      <img src={logo} alt="Logo" className="terms-logo" />
      <h1>Terms and Conditions</h1>
      <p>Welcome to Robert Motoring Consultâ€™s Car Rescue Service.</p>

      <ul>
        <li>All bookings must be confirmed with full payment before dispatch.</li>
        <li>We will not accept or participate in any illegal activity, and any such will be the responsibility of the person(s) hiring the service.</li>
        <li>Service availability depends on location and vehicle condition.</li>
        <li>For any complaints or clarifications, email us at: <strong>robertmotoringconsult@outlook.com</strong>.</li>
        <li>We reserve the right to refuse service based on our policy discretion.</li>
      </ul>

      <p>Thank you for choosing our service.</p>
    </div>
  );
};

export default Terms;
