import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { GoogleMap, LoadScript, Marker, DirectionsRenderer } from '@react-google-maps/api';
import axios from 'axios';
import logo from '../assets/logo.png';
import './BookingPage.css';

const containerStyle = {
  width: '100%',
  height: '400px'
};

const center = {
  lat: -1.286389,
  lng: 36.817223
};

const BookingPage = () => {
  const { register, handleSubmit, watch, setValue, formState: { errors } } = useForm();
  const [directions, setDirections] = useState(null);
  const [acceptedTerms, setAcceptedTerms] = useState(false);

  const onSubmit = async (data) => {
    if (!acceptedTerms) {
      alert("You must accept the terms and conditions to proceed.");
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/bookings', data);
      window.location.href = 'https://paystack.com/pay/hire-a-flatbed';
    } catch (error) {
      console.error('Error submitting booking:', error);
    }
  };

  const calculateRoute = () => {
    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin: watch('pickupLocation'),
        destination: watch('deliveryLocation'),
        travelMode: window.google.maps.TravelMode.DRIVING
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          setDirections(result);
        } else {
          console.error(`Error fetching directions ${result}`);
        }
      }
    );
  };

  return (
    <div className="booking-container">
      <img src={logo} alt="Logo" className="logo" />
      <h1>Car Rescue Service Booking</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h2>Service Details</h2>
        <label>Pickup Location:</label>
        <input {...register("pickupLocation", { required: true })} onBlur={calculateRoute} />
        {errors.pickupLocation && <span>This field is required</span>}

        <label>Delivery Location:</label>
        <input {...register("deliveryLocation", { required: true })} onBlur={calculateRoute} />
        {errors.deliveryLocation && <span>This field is required</span>}

        <label>Date From:</label>
        <input type="date" {...register("dateFrom", { required: true })} />

        <label>Date To:</label>
        <input type="date" {...register("dateTo", { required: true })} />

        <h2>Vehicle Details</h2>
        <label>Registration Number:</label>
        <input {...register("carRegistration", { required: true })} />

        <label>Vehicle Type:</label>
        <input {...register("carType", { required: true })} />

        <label>Weight (KG):</label>
        <input type="number" {...register("carWeight", { required: true })} />

        <label>Condition of Vehicle:</label>
        <textarea {...register("carCondition", { required: true })} />

        <h2>Contacts</h2>
        <label>Your Full Name:</label>
        <input {...register("requesterName", { required: true })} />

        <label>Your Phone Number:</label>
        <input {...register("requesterPhone", { required: true })} />

        <label>Driver Name (if different):</label>
        <input {...register("driverName")} />

        <label>Driver Phone Number:</label>
        <input {...register("driverPhone")} />

        <label>Other Instructions (Optional):</label>
        <textarea {...register("otherInstructions")} />

        <div className="terms-section">
          <input
            type="checkbox"
            id="acceptTerms"
            checked={acceptedTerms}
            onChange={() => setAcceptedTerms(!acceptedTerms)}
          />
          <label htmlFor="acceptTerms">
            I accept the <a href="/terms" target="_blank" rel="noopener noreferrer">View Terms and Conditions</a>
          </label>
        </div>

        <button type="submit" disabled={!acceptedTerms}>
          Confirm and Proceed to Payment
        </button>
      </form>

      <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY} libraries={['places']}>
        <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={7}>
          {directions && <DirectionsRenderer directions={directions} />}
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default BookingPage;
