import React from "react";
import flatbedBanner from "../assets/flatbed_banner.png"; // Make sure this path is correct
import "../styles/Home.css"; // Optional: Only include if you have custom styles

const Home = () => {
  return (
    <div className="home-page" style={{ textAlign: "center", padding: "2rem" }}>
      <h1>Welcome to Car Rescue Service</h1>
      <p>Book a flatbed truck for your car rescue anytime, anywhere.</p>

      <img
        src={flatbedBanner}
        alt="Flatbed Truck Banner"
        style={{
          width: "600px",
          height: "200px",
          objectFit: "cover",
          borderRadius: "12px",
          marginTop: "1rem",
        }}
      />

      <div style={{ marginTop: "2rem" }}>
        <a href="/booking">
          <button
            style={{
              padding: "0.75rem 1.5rem",
              fontSize: "16px",
              backgroundColor: "#b30000",
              color: "white",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            Book Now
          </button>
        </a>
      </div>
    </div>
  );
};

export default Home;
