import React from "react";
import "./Terms.css";

const Terms = () => {
  return (
    <div className="terms-container">
      <h1>Terms and Conditions</h1>
      <p>
        Please read these terms and conditions carefully before using our
        services. By using our services, you agree to the following:
      </p>
      <ul>
        <li>We will not accept or participate in any illegal activity, and any such activities will be the responsibility of the person/s hiring the service.</li>
        <li>Our service is available only for legitimate use and is intended to assist with vehicle recovery.</li>
        <li>Payments for bookings are non-refundable once the service has been initiated.</li>
        <li>We reserve the right to refuse service if we deem it unsafe or unlawful.</li>
      </ul>

      <p>If you have any questions, feel free to reach out to us via email at robertmotoringconsult@outlook.com.</p>
    </div>
  );
};

export default Terms;
