import React, { useState } from 'react';
import { GoogleMap, LoadScript, Marker, DirectionsService, DirectionsRenderer } from '@react-google-maps/api';
import './styles/MapContainer.css'; // Link to your MapContainer.css file

const containerStyle = {
  width: '100%',
  height: '500px',
};

const center = {
  lat: -1.286389,  // Adjust to the correct default center for your map
  lng: 36.817223,  // Adjust to the correct default center for your map
};

const MapContainer = () => {
  const [directionsResponse, setDirectionsResponse] = useState(null);
  const [pickupLocation, setPickupLocation] = useState('');
  const [deliveryLocation, setDeliveryLocation] = useState('');
  const [carDetails, setCarDetails] = useState({
    registration: '',
    type: '',
    weight: '',
  });

  const handleRouteChange = (e) => {
    setPickupLocation(e.target.value);
  };

  const handleDestinationChange = (e) => {
    setDeliveryLocation(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, use your logic to handle form submission or further operations
    alert("Booking submitted");
  };

  const handleCarDetailsChange = (e) => {
    const { name, value } = e.target;
    setCarDetails({
      ...carDetails,
      [name]: value,
    });
  };

  const handleDirectionsResponse = (response) => {
    if (response) {
      setDirectionsResponse(response);
    }
  };

  return (
    <div className="directions-container">
      <h2>Car Rescue Booking</h2>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Car Registration</label>
          <input
            type="text"
            name="registration"
            value={carDetails.registration}
            onChange={handleCarDetailsChange}
            required
          />
        </div>
        <div>
          <label>Car Type</label>
          <input
            type="text"
            name="type"
            value={carDetails.type}
            onChange={handleCarDetailsChange}
            required
          />
        </div>
        <div>
          <label>Car Weight</label>
          <input
            type="number"
            name="weight"
            value={carDetails.weight}
            onChange={handleCarDetailsChange}
            required
          />
        </div>

        <div>
          <label>Pickup Location</label>
          <input
            type="text"
            value={pickupLocation}
            onChange={handleRouteChange}
            required
          />
        </div>

        <div>
          <label>Delivery Location</label>
          <input
            type="text"
            value={deliveryLocation}
            onChange={handleDestinationChange}
            required
          />
        </div>

        <button type="submit">Complete Booking</button>
      </form>

      <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY"> {/* Replace with your actual key */}
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={12}
        >
          {pickupLocation && deliveryLocation && (
            <DirectionsService
              options={{
                origin: pickupLocation,
                destination: deliveryLocation,
                travelMode: 'DRIVING',
              }}
              callback={handleDirectionsResponse}
            />
          )}

          {directionsResponse && (
            <DirectionsRenderer
              options={{
                directions: directionsResponse,
              }}
            />
          )}

          <Marker position={center} />
        </GoogleMap>
      </LoadScript>
    </div>
  );
};

export default MapContainer;
