import React, { useState } from 'react';
import '../styles/BookingPage.css';  // Ensure the path is correct

const BookingPage = () => {
  const [isChecked, setIsChecked] = useState(false);
  const [formData, setFormData] = useState({
    userName: '',
    carRegistration: '',
    carType: '',
    carWeight: '',
    pickupLocation: '',
    deliveryLocation: '',
  });

  const handleCheckboxChange = (event) => {
    setIsChecked(event.target.checked);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!isChecked) {
      alert('You must accept the Terms and Conditions');
      return;
    }

    // Handle form submission here (for example, send data to backend or display success)
    console.log(formData);
  };

  return (
    <div className="booking-form">
      <h2>Book a Car Rescue Service</h2>
      <form onSubmit={handleSubmit}>
        <label>Name</label>
        <input
          type="text"
          name="userName"
          value={formData.userName}
          onChange={handleInputChange}
          required
        />

        <label>Car Registration Number</label>
        <input
          type="text"
          name="carRegistration"
          value={formData.carRegistration}
          onChange={handleInputChange}
          required
        />

        <label>Car Type</label>
        <input
          type="text"
          name="carType"
          value={formData.carType}
          onChange={handleInputChange}
          required
        />

        <label>Car Weight (KG)</label>
        <input
          type="number"
          name="carWeight"
          value={formData.carWeight}
          onChange={handleInputChange}
          required
        />

        <label>Pickup Location</label>
        <input
          type="text"
          name="pickupLocation"
          value={formData.pickupLocation}
          onChange={handleInputChange}
          required
        />

        <label>Delivery Location</label>
        <input
          type="text"
          name="deliveryLocation"
          value={formData.deliveryLocation}
          onChange={handleInputChange}
          required
        />

        <div>
          <input
            type="checkbox"
            id="terms"
            checked={isChecked}
            onChange={handleCheckboxChange}
          />
          <label htmlFor="terms">
            I accept the{' '}
            <a href="/terms" target="_blank" rel="noopener noreferrer">
              Terms and Conditions
            </a>
          </label>
        </div>

        <button type="submit">Submit Booking</button>
      </form>
    </div>
  );
};

export default BookingPage;
