import React, { useState } from 'react';
import { GoogleMap, useLoadScript, Marker, DirectionsRenderer } from '@react-google-maps/api';
import '../styles/MapContainer.css'; // Corrected path to the stylesheet

const containerStyle = {
  width: '100%',
  height: '500px',
};

const center = {
  lat: -1.286389, // Nairobi default
  lng: 36.817223,
};

const libraries = ['places'];

const MapContainer = () => {
  const { isLoaded } = useLoadScript({
    googleMapsApiKey: 'YOUR_GOOGLE_MAPS_API_KEY', // Replace with your actual key
    libraries,
  });

  const [pickup, setPickup] = useState('');
  const [destination, setDestination] = useState('');
  const [directions, setDirections] = useState(null);

  const handleRoute = () => {
    if (pickup && destination) {
      const directionsService = new window.google.maps.DirectionsService();
      directionsService.route(
        {
          origin: pickup,
          destination: destination,
          travelMode: window.google.maps.TravelMode.DRIVING,
        },
        (result, status) => {
          if (status === window.google.maps.DirectionsStatus.OK) {
            setDirections(result);
          } else {
            alert('Directions request failed: ' + status);
          }
        }
      );
    }
  };

  return isLoaded ? (
    <div className="map-wrapper">
      <h2 className="map-title">Request Rescue Service</h2>
      <div className="input-section">
        <input
          type="text"
          placeholder="Pickup Location"
          value={pickup}
          onChange={(e) => setPickup(e.target.value)}
        />
        <input
          type="text"
          placeholder="Destination"
          value={destination}
          onChange={(e) => setDestination(e.target.value)}
        />
        <button onClick={handleRoute}>Show Route</button>
      </div>

      <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={10}>
        {directions && <DirectionsRenderer directions={directions} />}
      </GoogleMap>
    </div>
  ) : (
    <p>Loading Map...</p>
  );
};

export default MapContainer;
