import React from "react";
import logo from "../assets/logo.png"; // Your logo path
import flatbedBanner from "../assets/flatbed_banner.png"; // Your banner image path
import "../styles/Home.css"; // Optional: Only if you want to use separate CSS

const Home = () => {
  return (
    <div className="home-page" style={{ textAlign: "center", padding: "2rem" }}>
      {/* Logo */}
      <img
        src={logo}
        alt="Robert Motoring Consult Logo"
        style={{
          width: "150px",
          height: "150px",
          objectFit: "contain",
          marginBottom: "1rem",
        }}
      />

      {/* Title & Tagline */}
      <h1>Welcome to Car Rescue Service</h1>
      <p>Book a flatbed truck for your car rescue anytime, anywhere.</p>

      {/* Flatbed Banner Image */}
      <img
        src={flatbedBanner}
        alt="Flatbed Truck Banner"
        style={{
          width: "600px",
          height: "200px",
          objectFit: "cover",
          borderRadius: "12px",
          marginTop: "1rem",
        }}
      />

      {/* Call to Action Button */}
      <div style={{ marginTop: "2rem" }}>
        <a href="/booking">
          <button
            style={{
              padding: "0.75rem 1.5rem",
              fontSize: "16px",
              backgroundColor: "#b30000", // Blood Red
              color: "white",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            Book Now
          </button>
        </a>
      </div>
    </div>
  );
};

export default Home;
