import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';  // Don't forget to import axios
import '../styles/BookingPage.css'; // Correct path to styles
import { sendEmail } from '../utils/emailService';  // Assuming you've created an email service utility

const BookingPage = () => {
  const { register, handleSubmit } = useForm(); // For form validation
  const [pickupLocation, setPickupLocation] = useState('');
  const [destinationLocation, setDestinationLocation] = useState('');
  const [vehicleDetails, setVehicleDetails] = useState({
    registrationNumber: '',
    vehicleType: '',
    weight: ''
  });
  const [termsAccepted, setTermsAccepted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Handle form submission
  const onSubmit = (data) => {
    console.log('Form Data:', data);

    if (!termsAccepted) {
      alert("Please accept the terms and conditions to proceed.");
      return;
    }

    setIsSubmitting(true);

    // API Integration
    axios.post(process.env.REACT_APP_API_URL, {
      pickupLocation: data.pickupLocation,
      destinationLocation: data.destinationLocation,
      vehicleDetails: {
        registrationNumber: data.registrationNumber,
        vehicleType: data.vehicleType,
        weight: data.weight
      },
      additionalInfo: data.additionalInfo,
      serviceDate: data.serviceDate,
      dateFrom: data.dateFrom,
      dateTo: data.dateTo
    })
    .then(response => {
      console.log('API Response:', response.data);
      sendEmail(data); // Send an email upon successful submission

      // Optional: Send message via WhatsApp using Twilio
      const message = `New booking from ${data.pickupLocation} to ${data.destinationLocation} for a ${data.vehicleType} with registration ${data.registrationNumber}. Service Date: ${data.serviceDate}`;
      axios.post(`https://api.twilio.com/2010-04-01/Accounts/${process.env.TWILIO_ACCOUNT_SID}/Messages.json`, {
        to: process.env.TWILIO_PHONE_NUMBER,
        from: process.env.TWILIO_PHONE_NUMBER,
        body: message
      });

      alert('Booking successful!');
      setIsSubmitting(false);
    })
    .catch(error => {
      console.error('API Error:', error);
      alert('There was an issue with your booking. Please try again later.');
      setIsSubmitting(false);
    });
  };

  return (
    <div className="booking-page">
      <img src="/src/assets/logo.png" alt="Robert Motoring Consult" className="logo" />
      <h1>Book a Flatbed Truck</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        {/* Pickup Location Input */}
        <div className="form-group">
          <label htmlFor="pickupLocation">Pickup Location:</label>
          <input
            type="text"
            id="pickupLocation"
            {...register('pickupLocation')}
            value={pickupLocation}
            onChange={(e) => setPickupLocation(e.target.value)}
            placeholder="Enter Pickup Location"
            required
          />
        </div>

        {/* Destination Location Input */}
        <div className="form-group">
          <label htmlFor="destinationLocation">Destination Location:</label>
          <input
            type="text"
            id="destinationLocation"
            {...register('destinationLocation')}
            value={destinationLocation}
            onChange={(e) => setDestinationLocation(e.target.value)}
            placeholder="Enter Destination Location"
            required
          />
        </div>

        {/* Vehicle Details Inputs */}
        <div className="form-group">
          <label htmlFor="registrationNumber">Vehicle Registration Number:</label>
          <input
            type="text"
            id="registrationNumber"
            {...register('registrationNumber')}
            value={vehicleDetails.registrationNumber}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, registrationNumber: e.target.value })}
            placeholder="Enter Vehicle Registration Number"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="vehicleType">Vehicle Type:</label>
          <input
            type="text"
            id="vehicleType"
            {...register('vehicleType')}
            value={vehicleDetails.vehicleType}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, vehicleType: e.target.value })}
            placeholder="Enter Vehicle Type"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="weight">Vehicle Weight:</label>
          <input
            type="number"
            id="weight"
            {...register('weight')}
            value={vehicleDetails.weight}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, weight: e.target.value })}
            placeholder="Enter Vehicle Weight"
            required
          />
        </div>

        {/* Date Inputs */}
        <div className="form-group">
          <label htmlFor="serviceDate">Service Date:</label>
          <input
            type="date"
            id="serviceDate"
            {...register('serviceDate')}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="dateFrom">Date From:</label>
          <input
            type="date"
            id="dateFrom"
            {...register('dateFrom')}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="dateTo">Date To:</label>
          <input
            type="date"
            id="dateTo"
            {...register('dateTo')}
            required
          />
        </div>

        {/* Terms and Conditions */}
        <div className="form-group">
          <label>
            <input
              type="checkbox"
              onChange={() => setTermsAccepted(!termsAccepted)}
              checked={termsAccepted}
            />
            I accept the <a href="/terms-and-conditions" target="_blank" rel="noopener noreferrer">Terms and Conditions</a>
          </label>
        </div>

        {/* Additional Info */}
        <div className="form-group">
          <label htmlFor="additionalInfo">Additional Information:</label>
          <textarea
            id="additionalInfo"
            {...register('additionalInfo')}
            placeholder="Any special instructions for pickup or delivery"
            rows="4"
          />
        </div>

        {/* Submit Button */}
        <div className="form-group">
          <button type="submit" disabled={isSubmitting || !termsAccepted}>
            {isSubmitting ? 'Submitting...' : 'Submit Booking'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default BookingPage;
