import React from 'react';
import '../styles/Terms.css'; // Correct path to the CSS file in src/styles/
import logo from '../assets/logo.png'; // Path to the logo image in src/assets/

const Terms = () => {
  return (
    <div className="terms-container">
      <div className="logo-container">
        <img src={logo} alt="Robert Motoring Consult" className="logo" />
      </div>
      <h1>Terms and Conditions</h1>
      <p>
        By using this service, you agree to the terms outlined below:
      </p>
      <ul>
        <li>The car recovery service is provided on-site and will be negotiated directly with the customer.</li>
        <li>Charges will be agreed upon before any recovery work is undertaken.</li>
        <li>Payment for the services is due after the service has been completed.</li>
      </ul>
    </div>
  );
};

export default Terms;
