import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios'; // Ensure axios is imported
import '../styles/BookingPage.css'; // Correct path to styles

// Assuming you have a function to send emails. If not, you can use a third-party service like Nodemailer or integrate with your backend.
import { sendEmail } from '../utils/emailService'; // Adjust this import to where your email service function is located

const BookingPage = () => {
  const { register, handleSubmit } = useForm(); // For form validation
  const [pickupLocation, setPickupLocation] = useState('');
  const [destinationLocation, setDestinationLocation] = useState('');
  const [vehicleDetails, setVehicleDetails] = useState({
    registrationNumber: '',
    vehicleType: '',
    weight: ''
  });
  const [acceptTerms, setAcceptTerms] = useState(false); // New state for terms and conditions

  // Handle form submission
  const onSubmit = (data) => {
    console.log('Form Data:', data);

    if (!acceptTerms) {
      alert('Please accept the terms and conditions before proceeding.');
      return;
    }

    // API Integration
    axios
      .post('YOUR_API_URL', {
        pickupLocation: data.pickupLocation,
        destinationLocation: data.destinationLocation,
        vehicleDetails: {
          registrationNumber: data.registrationNumber,
          vehicleType: data.vehicleType,
          weight: data.weight,
        },
        // Add other necessary details for the API
      })
      .then((response) => {
        console.log('API Response:', response.data);
        
        // Send email with the booking details to robertmotoringconsult@outlook.com
        sendEmail(data);
        
        // Optional: You could redirect the user or show a confirmation message here.
      })
      .catch((error) => {
        console.error('API Error:', error);
        alert('There was an error with your request. Please try again later.');
      });
  };

  return (
    <div className="booking-page">
      <h1>Book a Flatbed Truck</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        {/* Pickup Location Input */}
        <div className="form-group">
          <label htmlFor="pickupLocation">Pickup Location:</label>
          <input
            type="text"
            id="pickupLocation"
            {...register('pickupLocation')}
            value={pickupLocation}
            onChange={(e) => setPickupLocation(e.target.value)}
            placeholder="Enter Pickup Location"
          />
        </div>

        {/* Destination Location Input */}
        <div className="form-group">
          <label htmlFor="destinationLocation">Destination Location:</label>
          <input
            type="text"
            id="destinationLocation"
            {...register('destinationLocation')}
            value={destinationLocation}
            onChange={(e) => setDestinationLocation(e.target.value)}
            placeholder="Enter Destination Location"
          />
        </div>

        {/* Vehicle Details Inputs */}
        <div className="form-group">
          <label htmlFor="registrationNumber">Vehicle Registration Number:</label>
          <input
            type="text"
            id="registrationNumber"
            {...register('registrationNumber')}
            value={vehicleDetails.registrationNumber}
            onChange={(e) =>
              setVehicleDetails({
                ...vehicleDetails,
                registrationNumber: e.target.value,
              })
            }
            placeholder="Enter Vehicle Registration Number"
          />
        </div>

        <div className="form-group">
          <label htmlFor="vehicleType">Vehicle Type:</label>
          <input
            type="text"
            id="vehicleType"
            {...register('vehicleType')}
            value={vehicleDetails.vehicleType}
            onChange={(e) =>
              setVehicleDetails({
                ...vehicleDetails,
                vehicleType: e.target.value,
              })
            }
            placeholder="Enter Vehicle Type"
          />
        </div>

        <div className="form-group">
          <label htmlFor="weight">Vehicle Weight:</label>
          <input
            type="number"
            id="weight"
            {...register('weight')}
            value={vehicleDetails.weight}
            onChange={(e) =>
              setVehicleDetails({
                ...vehicleDetails,
                weight: e.target.value,
              })
            }
            placeholder="Enter Vehicle Weight"
          />
        </div>

        {/* Accept Terms and Conditions */}
        <div className="form-group">
          <label>
            <input
              type="checkbox"
              checked={acceptTerms}
              onChange={() => setAcceptTerms(!acceptTerms)}
            />
            I accept the Terms and Conditions.
          </label>
          <br />
          <a href="/terms-and-conditions" target="_blank" rel="noopener noreferrer">
            View Terms and Conditions
          </a>
        </div>

        {/* Submit Button */}
        <div className="form-group">
          <button type="submit" disabled={!acceptTerms}>
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default BookingPage;
