import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import '../styles/BookingPage.css'; // Correct path to styles
import logo from '../assets/logo.png'; // Logo path
import { Link } from 'react-router-dom'; // For navigation

const BookingPage = () => {
  const { register, handleSubmit } = useForm(); // For form validation
  const [pickupLocation, setPickupLocation] = useState('');
  const [destinationLocation, setDestinationLocation] = useState('');
  const [vehicleDetails, setVehicleDetails] = useState({
    registrationNumber: '',
    vehicleType: '',
    weight: ''
  });
  const [condition, setCondition] = useState('');
  const [termsAccepted, setTermsAccepted] = useState(false);
  const [paymentConfirmed, setPaymentConfirmed] = useState(false);
  const [viewTerms, setViewTerms] = useState(false);

  // Handle form submission
  const onSubmit = (data) => {
    if (!termsAccepted) {
      alert('You must accept the Terms and Conditions');
      return;
    }

    if (!paymentConfirmed) {
      alert('You must confirm payment');
      return;
    }

    console.log('Form Data:', data);
    // API Integration
    // Example API call using axios
    /*
    axios.post('YOUR_API_URL', {
      pickupLocation: data.pickupLocation,
      destinationLocation: data.destinationLocation,
      vehicleDetails: {
        registrationNumber: data.registrationNumber,
        vehicleType: data.vehicleType,
        weight: data.weight
      },
      condition: data.condition
    })
    .then(response => {
      // Handle the response after API call (e.g., show success message)
      console.log('API Response:', response.data);
    })
    .catch(error => {
      // Handle any errors from the API call
      console.error('API Error:', error);
    });
    */
  };

  return (
    <div className="booking-page">
      {/* Logo on top */}
      <div className="logo-container">
        <img src={logo} alt="Logo" className="logo" />
      </div>
      
      <h1>Book a Flatbed Truck</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        {/* Pickup Location Input */}
        <div className="form-group">
          <label htmlFor="pickupLocation">Pickup Location:</label>
          <input
            type="text"
            id="pickupLocation"
            {...register('pickupLocation')}
            value={pickupLocation}
            onChange={(e) => setPickupLocation(e.target.value)}
            placeholder="Enter Pickup Location"
          />
        </div>

        {/* Destination Location Input */}
        <div className="form-group">
          <label htmlFor="destinationLocation">Destination Location:</label>
          <input
            type="text"
            id="destinationLocation"
            {...register('destinationLocation')}
            value={destinationLocation}
            onChange={(e) => setDestinationLocation(e.target.value)}
            placeholder="Enter Destination Location"
          />
        </div>

        {/* Vehicle Details Inputs */}
        <div className="form-group">
          <label htmlFor="registrationNumber">Vehicle Registration Number:</label>
          <input
            type="text"
            id="registrationNumber"
            {...register('registrationNumber')}
            value={vehicleDetails.registrationNumber}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, registrationNumber: e.target.value })}
            placeholder="Enter Vehicle Registration Number"
          />
        </div>

        <div className="form-group">
          <label htmlFor="vehicleType">Vehicle Type:</label>
          <input
            type="text"
            id="vehicleType"
            {...register('vehicleType')}
            value={vehicleDetails.vehicleType}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, vehicleType: e.target.value })}
            placeholder="Enter Vehicle Type"
          />
        </div>

        <div className="form-group">
          <label htmlFor="weight">Vehicle Weight:</label>
          <input
            type="number"
            id="weight"
            {...register('weight')}
            value={vehicleDetails.weight}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, weight: e.target.value })}
            placeholder="Enter Vehicle Weight"
          />
        </div>

        {/* Condition of the Car Upon Collection */}
        <div className="form-group">
          <label htmlFor="carCondition">Condition of the Car Upon Collection:</label>
          <textarea
            id="carCondition"
            {...register('carCondition')}
            value={condition}
            onChange={(e) => setCondition(e.target.value)}
            placeholder="Brief description of the car's condition (e.g. Broken axle, no front wheels, etc.)"
            className="condition-textarea"
          />
        </div>

        {/* Terms and Conditions Section */}
        <div className="form-group">
          <div className="terms-container">
            <img src={logo} alt="Logo" className="logo-small" />
            <p>
              By booking, you accept our <a href="#" onClick={() => setViewTerms(true)}>Terms and Conditions</a>.
              For complaints, please email <a href="mailto:robertmotoringconsult@outlook.com">robertmotoringconsult@outlook.com</a>.
            </p>
            <input
              type="checkbox"
              id="termsAccepted"
              checked={termsAccepted}
              onChange={(e) => setTermsAccepted(e.target.checked)}
            />
            <label htmlFor="termsAccepted">I accept the Terms and Conditions</label>
          </div>
        </div>

        {/* View Full Terms and Conditions */}
        {viewTerms && (
          <div className="full-terms">
            <h3>Full Terms and Conditions</h3>
            <p>
              We will not accept or participate in any illegal activity, and any such activity will be the responsibility of the person/s hiring the service.
            </p>
            <p>
              These terms are in place to protect the safety and security of both the service provider and the client. You are legally responsible for ensuring the service is not used for any unlawful purposes.
            </p>
            <button onClick={() => setViewTerms(false)}>Close</button>
          </div>
        )}

        {/* Payment Section - Disabled until Terms are Accepted */}
        <div className="form-group">
          <p>Please confirm payment of the non-refundable deposit to confirm the booking.</p>
          <input
            type="checkbox"
            id="paymentConfirmed"
            checked={paymentConfirmed}
            onChange={(e) => setPaymentConfirmed(e.target.checked)}
            disabled={!termsAccepted}
          />
          <label htmlFor="paymentConfirmed" style={{ opacity: termsAccepted ? 1 : 0.5 }}>
            I confirm payment of the deposit
          </label>
        </div>

        {/* Submit Button */}
        <div className="form-group">
          <button type="submit" disabled={!termsAccepted || !paymentConfirmed}>Submit</button>
        </div>
      </form>

      {/* Optional: Redirect to Home page */}
      <div>
        <Link to="/">Back to Home</Link>
      </div>
    </div>
  );
};

export default BookingPage;
