import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import '../styles/BookingPage.css'; // Correct path to styles

const BookingPage = () => {
  const { register, handleSubmit } = useForm(); // For form validation
  const [pickupLocation, setPickupLocation] = useState('');
  const [destinationLocation, setDestinationLocation] = useState('');
  const [vehicleDetails, setVehicleDetails] = useState({
    registrationNumber: '',
    vehicleType: '',
    weight: ''
  });

  // Handle form submission
  const onSubmit = (data) => {
    console.log('Form Data:', data);
    
    // API Integration
    // Here, you can integrate your API call to send data to your backend or third-party service.
    // Example API call using axios:
    
    /*
    axios.post('YOUR_API_URL', {
      pickupLocation: data.pickupLocation,
      destinationLocation: data.destinationLocation,
      vehicleDetails: {
        registrationNumber: data.registrationNumber,
        vehicleType: data.vehicleType,
        weight: data.weight
      }
    })
    .then(response => {
      // Handle the response after API call (e.g., show success message)
      console.log('API Response:', response.data);
    })
    .catch(error => {
      // Handle any errors from the API call
      console.error('API Error:', error);
    });
    */
  };

  return (
    <div className="booking-page">
      <h1>Book a Flatbed Truck</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        {/* Pickup Location Input */}
        <div className="form-group">
          <label htmlFor="pickupLocation">Pickup Location:</label>
          <input
            type="text"
            id="pickupLocation"
            {...register('pickupLocation')}
            value={pickupLocation}
            onChange={(e) => setPickupLocation(e.target.value)}
            placeholder="Enter Pickup Location"
          />
        </div>

        {/* Destination Location Input */}
        <div className="form-group">
          <label htmlFor="destinationLocation">Destination Location:</label>
          <input
            type="text"
            id="destinationLocation"
            {...register('destinationLocation')}
            value={destinationLocation}
            onChange={(e) => setDestinationLocation(e.target.value)}
            placeholder="Enter Destination Location"
          />
        </div>

        {/* Vehicle Details Inputs */}
        <div className="form-group">
          <label htmlFor="registrationNumber">Vehicle Registration Number:</label>
          <input
            type="text"
            id="registrationNumber"
            {...register('registrationNumber')}
            value={vehicleDetails.registrationNumber}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, registrationNumber: e.target.value })}
            placeholder="Enter Vehicle Registration Number"
          />
        </div>

        <div className="form-group">
          <label htmlFor="vehicleType">Vehicle Type:</label>
          <input
            type="text"
            id="vehicleType"
            {...register('vehicleType')}
            value={vehicleDetails.vehicleType}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, vehicleType: e.target.value })}
            placeholder="Enter Vehicle Type"
          />
        </div>

        <div className="form-group">
          <label htmlFor="weight">Vehicle Weight:</label>
          <input
            type="number"
            id="weight"
            {...register('weight')}
            value={vehicleDetails.weight}
            onChange={(e) => setVehicleDetails({ ...vehicleDetails, weight: e.target.value })}
            placeholder="Enter Vehicle Weight"
          />
        </div>

        {/* Submit Button */}
        <div className="form-group">
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default BookingPage;
